import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Product from '../Section/Product/Product';
import '../../css/Product.css';

function WorkCategorypage({match}) {
  const i = match.params.id;
  const [input, setInput] = useState([]);
  useEffect(() => {
    try {
    axios
    .get(`/api/w/category/${i}`)
    .then(function(response) {
      const resData = response.data.response;
      setInput(resData);
    })
    } catch (e) {
    console.log('error');
    }
  }, []);
  return (
        <ContentDetailDiv>
          <TextWrapper>
            <ContentDiv>
              <h4>오늘의 발견</h4>
              <h5>아이디어스가 추천하는 작품을 추천드려요</h5>
              </ContentDiv>
          </TextWrapper>
          {input.slice(0, 10).map((a, i) => {
            return <Product product={input[i]} i={i} key={i} />;
          })}
        </ContentDetailDiv>
  );
}

export default WorkCategorypage;


const ContentDetailDiv = styled.div`
  width: 1130px;
  height: 100vh;
`;

const TextWrapper = styled.div`
  width: 1800px;
  height: 19vh;
  background: black;
  color: white;
  overflow: visible;
  margin: 0% 0% 0% 0%;
`;

const ContentDiv = styled.div`
  padding: 3% 0% 0% 30%;
`;
