                        <h4>예약 당일 밤 12시 이전 취소 시</h4>
                        <h5><Green>100%</Green> 환불</h5>
                    </Addr>
                    <Review>
                        <p>참여후기</p>
                        {
                        reviews.map((a, i) => {
                            return (
                                <div>
                                <h1>{reviews[i].name}</h1>
                                <h2>{reviews[i].date}</h2>
                                <h3>{reviews[i].content}</h3>
                                </div>
                            );
                        })
                        }
                    </Review>
                    <Comment>
                        <p>댓글</p>
                    </Comment>
                </Information_C>
            </LeftContent>
            <RightContent>
                Payment
            </RightContent>
        </ClassDetail>
    )
}

