import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import {Link} from 'react-router-dom';
import Silder from '../ImgSlider/Silder';
// viewport importing 
import handleViewport from "react-in-viewport";
// CSS
import '../../css/WorkPage.css';
import '../../css/Product.css';

// 메인 페이지 섹션 별 Components 
import Hit from'./WorkMainCategory/Hit.js';
import Price from'./WorkMainCategory/Price.js';
import Purchase from'./WorkMainCategory/Purchase.js';
import Rate from'./WorkMainCategory/MainRate.js';
import MainCategory from'./WorkMainCategory/MainCategory';


const Item = ({ inViewport, enterCount, leaveCount, innerRef }) => {
  const getStyle = () => {
    // Fade in only the first time we enter the viewport.
    if (inViewport && enterCount === 1) {
      return { WebkitTransition: "opacity 0.75s ease-in-out" };
    } else if (!inViewport && enterCount < 1) {
      return { WebkitTransition: "none", opacity: "0" };
    } else {
      return {};
    }
  };

  return (
    <div className="content" style={getStyle()} ref={innerRef}>
      <h3>Hello Viewport!</h3>
      <p>{`Enter viewport: ${enterCount} times`}</p>
      <p>{`Leave viewport: ${leaveCount} times`}</p>
    </div>
  );
};

const ViewportItem = handleViewport(Item /** options: {}, config: {} **/);

function WorkPage() {
  return (
    <div className="WorkPage">
      <ViewportItem>
      <nav className="promotion">
        <Silder />
      </nav>

      <ContentDiv>
        <ContentDetailDiv>
          <Rate></Rate>
        </ContentDetailDiv>

        <ContentDetailDiv>
          <Price></Price>
        </ContentDetailDiv>

        <ContentDetailDiv>
          <Hit></Hit>
        </ContentDetailDiv>

        <ContentDetailDiv>
          <Purchase></Purchase>
        </ContentDetailDiv>

        <ContentDetailDiv>
          <MainCategory></MainCategory>
        </ContentDetailDiv>
      </ContentDiv>
      </ViewportItem>
    </div>
  );
}

export default WorkPage;

const ContentDiv = styled.div`
  padding: 2% 20%;
  hr {
    width: 90%;
  }
  h4 {
    font-weight: bold;
  }
`;

const ContentDetailDiv = styled.div`
  width: 1130px;
`;

const CategoryDiv = styled.div`
  appearance: none;
  display: inline-block;
  width: 10.5%;
  text-align: center;
  margin: 10px;
  padding: 5px 0px;
  border: 1px solid lightgrey;
  border-radius: 10%;
  background-color: white;
`;

const CategoryTag = styled.a`
  color: black;
  font-size: 12px;
  top: 10px;
  text-decoration: none;
`;
