import axios from 'axios';

class AuthenticationService {
    // send username, password to the SERVER
    PostSignupInfo(email, name, password,tel,writerFlag) {
        const data = {
            email, 
            name, 
            password,
            tel, 
            writerFlag
          }
        return axios.post('api/user/join', data)
    }

    PostLoginInfo(email, password) {
        const data = {
            email,
            password
        }
        return axios.post('api/user/login', data)
    }

    executeAddAddress(name,phone,zipcode,addressMain,addressSub) {
        const token = localStorage.getItem('token');
        const data = {
            name,
            phone,
            zipcode,
            addressMain,
            addressSub
          }
        return axios.post('api/user/join', data)
    }

    registerSuccessfulLoginForJwt(username, token) {
        console.log("===registerSuccessfulLoginForJwt===")
        localStorage.setItem('token', token);
        localStorage.setItem('authenticatedUser', username);
    }

    logout() {
        localStorage.removeItem("authenticatedUser");
        localStorage.removeItem("token");
    }

    isUserLoggedIn() {
        const token = localStorage.getItem('token');
        console.log("===UserloggedInCheck===");
        console.log(token);

        if (token) {
            return true;
        }

        return false;
    }

    withdrawUser() {
        const token = localStorage.getItem('token');
        return axios.delete('api/t/user/delete', {
            token
        })
    }
    sendUserToken() {
        const token = localStorage.getItem('token');
        return axios.get('/api/t/user/address', {
            token
        })
    }

    getLoggedInUserName() {
        //let user = sessionStorage.getItem('authenticatedUser')
        let user = localStorage.getItem('authenticatedUser');
        if(user===null) return '';
        return user;
    }
}

export default new AuthenticationService()
