import React from 'react';
import styled from 'styled-components';
import starImg from '../../../img/stars.png';
import CategoryImg from '../../../img/CategoryImg.png'


function ClassHot(props) {
  return (
    <Wrapper>
      <ImageBox>
        <img src={CategoryImg} /> {/* 이미지를 받아와서, hover impact를 주어야 한다. */}
      </ImageBox>
      <TextWrapper>
        <ShopTag>바느질/재봉 - 스태리라운지</ShopTag>
        <h5>펀치니들로 만드는 유니크한 미니매트</h5>
        <h4>80% 9900원 / 월</h4>
      </TextWrapper>
    </Wrapper>
  );
}

export default ClassHot;



const Wrapper = styled.div`
  width: 42%;
  display:flex;
  margin: 0% 1% 5% 1%;
  padding: 10px;
  display: inline-block;
  background-color: rgb(247, 246, 250);
  border: none;
  hr {
    width: 100%;
  }
`;

const TextWrapper = styled.div`
  h5 {
    font-size: 14px;
  }
  h4 {
    font-size: 16px;
  }
`;


const ShopTag = styled.a`
  color: black;
  font-size: 12px;
  top: 10px;
  text-decoration: none;
`;

const ImageBox = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 150px;
  margin: 0% 0%; 0% 00%;
  overflow: hidden;
  border-radius: 4px;
  transform: scale(1.14);
  transition: transform 0.5s ease;

`;
